/**
 * @file Interfaces - Result
 * @module mkbuild/interfaces/Result
 */

import type { OutputMetadata } from '#src/types'
import type * as esbuild from 'esbuild'

/**
 * Object representing a build result.
 *
 * @extends {esbuild.OutputFile}
 */
interface Result extends esbuild.OutputFile {
  /**
   * Output file size.
   */
  bytes: OutputMetadata['bytes']

  /**
   * Relative path to source file.
   *
   * **Note**: Relative to current working directory.
   */
  entryPoint: OutputMetadata['entryPoint']

  /**
   * Error messages generated by [esbuild][1].
   *
   * [1]: https://esbuild.github.io
   */
  errors: esbuild.BuildResult['errors']

  /**
   * Export names.
   */
  exports: OutputMetadata['exports']

  /**
   * Import names.
   */
  imports: OutputMetadata['imports']

  /**
   * Source file map.
   */
  inputs: esbuild.Metafile['inputs']

  /**
   * Relative path to output file.
   *
   * **Note**: Relative to current working directory.
   */
  outfile: string

  /**
   * Warning messages generated by [esbuild][1].
   *
   * [1]: https://esbuild.github.io
   */
  warnings: esbuild.BuildResult['warnings']
}

export type { Result as default }
